mapper.insert:
INSERT INTO celebs(
	celeb_code
	, stage_name
	, stage_name_en
	, real_name
	, real_name_en
	, gender
	, birth_date
	, debut_year
	, country
	, country_en
	, state
	, city
	, height
	, weight
	, company
) VALUES(
	#{celebCode}
	, #{stageName}
	, #{stageNameEn}
	, #{realName}
	, #{realNameEn}
	, #{gender}
	, #{birthDate}
	, #{debutYear}
	, #{country}
	, #{countryEn}
	, #{state}
	, #{city}
	, #{height}
	, #{weight}
	, #{company}
)

mapper.select:
SELECT celeb_seq , celeb_code , stage_name , stage_name_en , real_name , real_name_en , gender , birth_date , debut_year , country , country_en , state , city , height , weight , company ,updated FROM celebs
AND stage_name LIKE CONCAT('%', #{searchParam.keyword}, '%')
ORDER BY stage_name ASC LIMIT #{pageRequest.limit}, #{pageRequest.offset}
